#!/bin/bash\n\n# SoulSync Admin Dashboard Setup Script\necho \"🏥 SoulSync Admin Dashboard Setup\"\necho \"====================================\"\necho \"\"\n\n# Check if Node.js is installed\nif ! command -v node &> /dev/null; then\n    echo \"❌ Node.js is not installed. Please install Node.js v16 or higher.\"\n    exit 1\nfi\n\necho \"✅ Node.js found: $(node --version)\"\n\n# Check if npm is installed\nif ! command -v npm &> /dev/null; then\n    echo \"❌ npm is not installed. Please install npm.\"\n    exit 1\nfi\n\necho \"✅ npm found: $(npm --version)\"\necho \"\"\n\n# Install backend dependencies\necho \"📦 Installing backend dependencies...\"\ncd backend\nnpm install bcrypt jsonwebtoken\nif [ $? -ne 0 ]; then\n    echo \"❌ Failed to install backend dependencies\"\n    exit 1\nfi\necho \"✅ Backend dependencies installed\"\ncd ..\n\n# Install frontend dependencies\necho \"📦 Installing frontend dependencies...\"\nnpm install recharts\nif [ $? -ne 0 ]; then\n    echo \"❌ Failed to install frontend dependencies\"\n    exit 1\nfi\necho \"✅ Frontend dependencies installed\"\necho \"\"\n\n# Check for .env file\nif [ ! -f \"backend/.env\" ]; then\n    echo \"⚠️  Warning: backend/.env file not found\"\n    echo \"Please create backend/.env with the following variables:\"\n    echo \"MONGO_URI=your-mongodb-connection-string\"\n    echo \"JWT_SECRET=your-jwt-secret-key\"\n    echo \"\"\nelse\n    echo \"✅ Environment file found\"\nfi\n\n# Seed database\necho \"🌱 Seeding admin data...\"\ncd backend\nnode seedAdminData.js\nif [ $? -ne 0 ]; then\n    echo \"❌ Failed to seed database. Please check your MongoDB connection.\"\n    echo \"Make sure MONGO_URI is correctly set in backend/.env\"\n    exit 1\nfi\ncd ..\n\necho \"\"\necho \"🎉 Setup completed successfully!\"\necho \"\"\necho \"📋 Next steps:\"\necho \"1. Start the backend server: cd backend && npm start\"\necho \"2. In a new terminal, start frontend: npm run dev\"\necho \"3. Access admin dashboard: http://localhost:5173/admin-login\"\necho \"\"\necho \"🔐 Admin Login Credentials:\"\necho \"Super Admin: admin@institution.edu / password123\"\necho \"Counsellor: dr.smith@institution.edu / password123\"\necho \"Dept Admin: welfare@institution.edu / password123\"\necho \"\"\necho \"📖 For detailed instructions, see ADMIN_SETUP_README.md\"\n